import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { blogAPI } from '../services/api';

const Home = () => {
  const [blogs, setBlogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBlogs(currentPage);
  }, [currentPage]);

  const fetchBlogs = async (page) => {
    try {
      const response = await blogAPI.getBlogs(page, 5);
      setBlogs(response.data.content || []);
      setTotalPages(response.data.totalPages || 0);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching blogs:', error);
      setBlogs([]);
      setLoading(false);
    }
  };

  if (loading) return <div style={{ textAlign: 'center', padding: '2rem' }}>Loading...</div>;

  return (
    <div style={{ maxWidth: '800px', margin: '2rem auto', padding: '0 1rem' }}>
      <h1>All Blogs</h1>
      {!blogs || blogs.length === 0 ? (
        <p>No blogs found.</p>
      ) : (
        <>
          {blogs && blogs.map(blog => (
            <div key={blog.id} style={{ 
              border: '1px solid #ddd', 
              padding: '1rem', 
              marginBottom: '1rem',
              borderRadius: '5px'
            }}>
              <h3>
                <Link to={`/blog/${blog.id}`} style={{ textDecoration: 'none', color: '#007bff' }}>
                  {blog.title}
                </Link>
              </h3>
              <p style={{ color: '#666', fontSize: '0.9rem' }}>
                By {blog.author?.email || 'Unknown author'} on {blog.createdAt ? new Date(blog.createdAt).toLocaleDateString() : 'Unknown date'}
              </p>
              <p>{blog.content.substring(0, 200)}...</p>
            </div>
          ))}

          <div style={{ textAlign: 'center', marginTop: '2rem' }}>
            <button 
              onClick={() => setCurrentPage(prev => Math.max(0, prev - 1))}
              disabled={currentPage === 0}
              style={{ 
                padding: '0.5rem 1rem', 
                marginRight: '0.5rem',
                background: currentPage === 0 ? '#ccc' : '#007bff',
                color: 'white',
                border: 'none',
                cursor: currentPage === 0 ? 'not-allowed' : 'pointer'
              }}
            >
              Previous
            </button>
            <span style={{ margin: '0 1rem' }}>
              Page {currentPage + 1} of {totalPages}
            </span>
            <button 
              onClick={() => setCurrentPage(prev => Math.min(totalPages - 1, prev + 1))}
              disabled={currentPage >= totalPages - 1}
              style={{ 
                padding: '0.5rem 1rem', 
                marginLeft: '0.5rem',
                background: currentPage >= totalPages - 1 ? '#ccc' : '#007bff',
                color: 'white',
                border: 'none',
                cursor: currentPage >= totalPages - 1 ? 'not-allowed' : 'pointer'
              }}
            >
              Next
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Home;
