import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import CreateBlog from './pages/CreateBlog';
import BlogDetail from './pages/BlogDetail';
import EditBlog from './pages/EditBlog';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const email = localStorage.getItem('email');
    if (token && email) {
      setUser(email);
    }
  }, []);

  return (
    <Router>
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
        fontFamily: "'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
      }}>
        <Navbar user={user} setUser={setUser} />

        <div style={{
          paddingTop: '80px', // Space for fixed navbar
          minHeight: 'calc(100vh - 80px)'
        }}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login setUser={setUser} />} />
            <Route path="/register" element={<Register setUser={setUser} />} />
            <Route path="/create" element={<CreateBlog user={user} />} />
            <Route path="/blog/:id" element={<BlogDetail user={user} />} />
            <Route path="/edit/:id" element={<EditBlog user={user} />} />
          </Routes>
        </div>

        {/* Global styles for smooth scrolling and modern look */}
        <style jsx global>{`
          * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
          }

          body {
            line-height: 1.6;
            color: #333;
            scroll-behavior: smooth;
          }

          a {
            text-decoration: none;
            transition: all 0.3s ease;
          }

          button {
            transition: all 0.3s ease;
          }

          input, textarea {
            transition: all 0.3s ease;
          }

          /* Custom scrollbar */
          ::-webkit-scrollbar {
            width: 8px;
          }

          ::-webkit-scrollbar-track {
            background: #f1f1f1;
          }

          ::-webkit-scrollbar-thumb {
            background: #667eea;
            border-radius: 4px;
          }

          ::-webkit-scrollbar-thumb:hover {
            background: #5a6fd8;
          }
        `}</style>
      </div>
    </Router>
  );
}

export default App;
