import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { blogAPI } from '../services/api';

const EditBlog = ({ user }) => {
  const { id } = useParams();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchBlog();
  }, [id]);

  const fetchBlog = async () => {
    try {
      const response = await blogAPI.getBlog(id);
      const blog = response.data;

      if (user !== blog.author.email) {
        navigate('/');
        return;
      }

      setTitle(blog.title);
      setContent(blog.content);
      setLoading(false);
    } catch (error) {
      setError('Blog not found');
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await blogAPI.updateBlog(id, title, content);
      navigate(`/blog/${id}`);
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to update blog');
    }
  };

  if (!user) {
    return (
      <div style={{ textAlign: 'center', padding: '2rem' }}>
        <p>Please login to edit blogs.</p>
      </div>
    );
  }

  if (loading) return <div style={{ textAlign: 'center', padding: '2rem' }}>Loading...</div>;

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto', padding: '0 1rem' }}>
      <h2>Edit Blog</h2>
      {error && <div style={{ color: 'red', marginBottom: '1rem' }}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '1rem' }}>
          <input
            type="text"
            placeholder="Blog Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{ width: '100%', padding: '0.75rem', fontSize: '1rem' }}
          />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <textarea
            placeholder="Blog Content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
            rows="10"
            style={{ width: '100%', padding: '0.75rem', fontSize: '1rem', resize: 'vertical' }}
          />
        </div>
        <button type="submit" style={{ 
          padding: '0.75rem 2rem', 
          background: '#007bff', 
          color: 'white', 
          border: 'none',
          fontSize: '1rem',
          cursor: 'pointer',
          marginRight: '1rem'
        }}>
          Update Blog
        </button>
        <button 
          type="button" 
          onClick={() => navigate(`/blog/${id}`)}
          style={{ 
            padding: '0.75rem 2rem', 
            background: '#6c757d', 
            color: 'white', 
            border: 'none',
            fontSize: '1rem',
            cursor: 'pointer'
          }}
        >
          Cancel
        </button>
      </form>
    </div>
  );
};

export default EditBlog;
